import React,{useState} from "react";

const BasicForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [allEntery, setAllEntery] = useState([]);
    
    const submitForm = () => {
        const newEntery = {email: email, password: password};
        setAllEntery([...allEntery,newEntery])
    }

  return (
    <>
    <form action="" onSubmit ={submitForm}>
        <div>
            <label htmlFor="email">Email</label>
            <input type = "text" name="email" id="email" autoComplete="off" value={email} onChange ={(e)=>setEmail(e.target.value)}></input>
        </div>

        <div>
            <label htmlFor="password">Password</label>
            <input type = "password" name="password" id="password" value={password} onChange ={(e)=>setPassword(e.target.value)}></input>
        </div>

        <button type="submit">Login</button>

    </form>
    </>
  );
};

export default BasicForm;
